package com.weeklycontest2;

import java.util.BitSet;

/**
 * Created by krutarthjoshi on 8/28/16.
 */
public class FindtheDifference {

    public static void main(String[] args) {
        FindtheDifference ftd = new FindtheDifference();
        String s = "";
        String t = "";
        System.out.println(ftd.findTheDifference(s, t));
        System.out.println(ftd.findTheDifference("abc", "abcd"));
    }

    private char findTheDifference(String s, String t) {
        BitSet chars = new BitSet();
        for (int i = 0; i < t.length(); i++) {
            chars.flip(t.charAt(i) - 'a');
        }
        for (int i = 0; i < s.length(); i++) {
            chars.flip(s.charAt(i) - 'a');
        }
        return (char) (chars.nextSetBit(0) + 'a');
    }
}
